// src/components/ChatAssistant.js
import React, { useState } from 'react';
import ChatInput from './ChatInput';

function ChatAssistant() {
  const [messages, setMessages] = useState([
    { text: 'Hi there! How can I help you today?', sender: 'assistant' }
  ]);
  const [recommendedProduct, setRecommendedProduct] = useState(null);
  const [isPurchasing, setIsPurchasing] = useState(false);
  const [orderDetails, setOrderDetails] = useState({ name: '', address: '', paymentInfo: '' });

  const handleSendMessage = async (newMessage) => {
    setMessages((prevMessages) => [...prevMessages, { text: newMessage, sender: 'user' }]);

    try {
        const response = await fetch('http://localhost:5005/webhooks/rest/webhook', {  // Rasa's default port
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ sender: 'user', message: newMessage }),
        });

        const data = await response.json();
        // Handle the Rasa response
        data.forEach(msg => {
            setMessages((prevMessages) => [
                ...prevMessages,
                { text: msg.text, sender: 'assistant' },
            ]);
        });
    } catch (error) {
        console.error('Error sending message:', error);
    }
};
  const handlePurchase = async () => {
    try {
      const response = await fetch('http://localhost:3030/api/chat/purchase', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name: orderDetails.name,
          address: orderDetails.address,
          paymentInfo: orderDetails.paymentInfo,
          productId: recommendedProduct._id // Ensure this matches your MongoDB product ID field
        }),
      });

      const data = await response.json();
      setMessages((prevMessages) => [
        ...prevMessages,
        { text: `Your order number is ${data.orderNumber}`, sender: 'assistant' },
      ]);
      setRecommendedProduct(null); // Reset recommendation
      setOrderDetails({ name: '', address: '', paymentInfo: '' }); // Reset order details
      setIsPurchasing(false);
    } catch (error) {
      console.error('Error processing purchase:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setOrderDetails(prevDetails => ({ ...prevDetails, [name]: value }));
  };

  return (
    <div className="chat-assistant-container">
      <div className="chat-box">
        {messages.map((message, index) => (
          <div key={index} className={`message ${message.sender}`}>
            {message.text}
          </div>
        ))}
      </div>
      <ChatInput onSendMessage={handleSendMessage} />

      {recommendedProduct && !isPurchasing && (
        <div className="recommendation">
          <h2>Recommended Product:</h2>
          <p>{recommendedProduct.name} - ${recommendedProduct.price}</p>
          <button onClick={() => setIsPurchasing(true)}>Purchase</button>
        </div>
      )}

      {isPurchasing && (
        <div className="purchase-form">
          <h3>Enter Your Details to Complete the Purchase</h3>
          <input type="text" name="name" placeholder="Name" onChange={handleInputChange} />
          <input type="text" name="address" placeholder="Shipping Address" onChange={handleInputChange} />
          <input type="text" name="paymentInfo" placeholder="Payment Info" onChange={handleInputChange} />
          <button onClick={handlePurchase}>Submit Purchase</button>
        </div>
      )}
    </div>
  );
}

export default ChatAssistant;
